var countCharactersBlogpost;
var countCharacters;

function Editor_Blogpost() {
    CKEDITOR.ClassicEditor.create(document.getElementById("Editor_Blogpost"), {
        toolbar: {
            items: ['bold', 'italic', 'underline', '|', 'outdent', 'indent', '|', 'undo', 'redo',],
            shouldNotGroupWhenFull: true
        },
        language: 'th',
        placeholder: 'คุณกำลังคิดอะไรอยู่',
        fontFamily: {
            options: [
                'Kanit', 'sans-serif'
            ],
            supportAllValues: true
        },
        removePlugins: [
            'CKBox',
            'CKFinder',
            'EasyImage',
            'RealTimeCollaborativeComments',
            'RealTimeCollaborativeTrackChanges',
            'RealTimeCollaborativeRevisionHistory',
            'PresenceList',
            'Comments',
            'TrackChanges',
            'TrackChangesData',
            'RevisionHistory',
            'Pagination',
            'WProofreader',
            'MathType',
            'SlashCommand',
            'Template',
            'DocumentOutline',
            'FormatPainter',
            'TableOfContents',
            'PasteFromOfficeEnhanced'
        ],
        wordCount: {
            onUpdate: stats => {
                countCharactersBlogpost = stats.characters;
            }
        }
    }).then(newEditor => {
        editorblogpost = newEditor;
        editorblogpost.model.document.on('change:data', () => {
            if (countCharactersBlogpost > 0) {
                if (editorblogpost.getData() != '') {
                    $('.btn_post_blogpost').attr('disabled', false);
                }
                else {
                    $('.btn_post_blogpost').attr('disabled', true);
                }
            }
            else {
                var files = $('.uploadimagevideofile_blogpostmodal').prop('files');
                if (files.length > 0) {
                    $('.btn_post_blogpost').attr('disabled', false);
                }
                else {
                    $('.btn_post_blogpost').attr('disabled', true);
                }
            }
        });
    }).catch(error => {
        console.error(error);
    });
}

