function SpliceArrayFolder(array, pathfolder, name) {
    var result;
    for (var i = 0; i < array.length; i++) {
        if (array[0] == 'wwwroot' && array[1] == 'Attachment' && array[2] == pathfolder) {
            if (array[i].includes(name)) {
                result = array[i];
            }
        }
    }
    return result;
}

function CheckArrayFolderName(array, foldername) {
    var resultarray = 0;
    var result;

    for (var i = 1; i <= array.length + 1 || resultarray == -1; i++) {
        if (i == 1) {
            var checkfoldername = foldername;

            if ($.inArray(checkfoldername, array) == -1) {
                result = checkfoldername;
            }
            else {
                result = null;//duplicate
            }
        }
        else {
            var checkfoldername = foldername + ' (' + i + ')';

            if ($.inArray(checkfoldername, array) == -1) {
                result = checkfoldername;
            }
            else {
                result = null;//duplicate
            }
        }
    }

    return result;
}

