async function CheckCreatePost() {
    var title = $('title').text().split(' ');
    if (title.length > 3) {
        title = title[0] + ' ' + title[1];
    }
    else {
        title = title[0];
    }

    var useridprofile = $('#useridprofile').val();

    var UserMenuGroupIdFunction = await UserMenuGroupId(useridprofile);
    if (UserMenuGroupIdFunction != null) {

        var MenuGroupIdFunction = await MenuGroupId(UserMenuGroupIdFunction.menuGroupId);
        if (MenuGroupIdFunction != null) {
            $.each(MenuGroupIdFunction, function (i, menugroupiddata) {
                if (menugroupiddata.name != title) {
                    $('.blogpost').attr('hidden', true);
                    $('.lifilemanage').attr('hidden', true);
                }
                else {
                    $('.blogpost').attr('hidden', false);
                    $('.lifilemanage').attr('hidden', false);
                }
            });
        }
        else {
            $('.blogpost').attr('hidden', true);
            $('.lifilemanage').attr('hidden', true);
        }
    }
    else {
        $('.blogpost').attr('hidden', true);
        $('.lifilemanage').attr('hidden', true);
    }
}


