function DisplayGeneralDeletePost(id) {
    var title = $('title').text().split(' ');
    if (title.length > 3) {
        title = title[0] + ' ' + title[1];
    }
    else {
        title = title[0];
    }

    CheckActionPostId(id)
        .then(function (CheckActionPostIdFucntion) {
            if (CheckActionPostIdFucntion != null) {
                if (CheckActionPostIdFucntion.action == 'Delete') {

                    MenuGroupId(CheckActionPostIdFucntion.menuGroupId)
                        .then(function (MenuGroupIdFunction) {
                            if (MenuGroupIdFunction.length > 0) {
                                $.each(MenuGroupIdFunction, function (i, menugroupiddata) {
                                    if (menugroupiddata.name == title) {
                                        $('.blogpostfeed').find('.blogpost#' + CheckActionPostIdFucntion.postId).remove();
                                    }
                                    else if (title == 'Home') {
                                        $('.blogpostfeed').find('.blogpost#' + CheckActionPostIdFucntion.postId).remove();
                                    }
                                });
                            }
                        })
                        .catch(function (error) {
                            console.error(error);
                            throw error;
                        });
                }
            }
        })
        .catch(function (error) {
            console.error(error);
            throw error;
        });
}

