function checknumbercount(numbercount) {
    var numbercounttext = numbercount.toString();
    var numbercounttextlength = numbercounttext.length;
    var numberunit;
    var sumnumbercounttextlength;

    if (numbercounttextlength >= 8) {
        numberunit = ' ล้าน';
        var checknumbercountzero = numbercounttext.substring(numbercounttextlength - 6, numbercounttextlength - 5);
        if (checknumbercountzero == '0') {
            var resultnumbercounttext = numbercounttext.substring(0, numbercounttextlength - 6) + numberunit;
            return resultnumbercounttext;
        }
        else {
            sumnumbercounttextlength = numbercounttextlength - (numbercounttextlength - (numbercounttextlength - 5));
        }
    }
    else if (numbercounttextlength >= 4) {
        if (numbercounttextlength == 7) {
            numberunit = ' ล้าน';
        }
        else if (numbercounttextlength == 6) {
            numberunit = ' แสน';
        }
        else if (numbercounttextlength == 5) {
            numberunit = ' หมื่น';
        }
        else if (numbercounttextlength == 4) {
            numberunit = ' พัน';
        }
        var checknumbercountzero = numbercounttext.substring(1, 2);
        if (checknumbercountzero == '0') {
            var resultnumbercounttext = numbercounttext.substring(0, 1) + numberunit;
            return resultnumbercounttext;
        }
        else {
            sumnumbercounttextlength = numbercounttextlength - (numbercounttextlength - 2);
        }
    }
    else {
        return numbercount;
    }

    var resultsumnumbercounttextlength = numbercounttext.substring(0, sumnumbercounttextlength);
    var sumresultsumnumbercounttextlength = resultsumnumbercounttextlength.length - 1;
    var arrayresultsumnumbercounttextlength = resultsumnumbercounttextlength.split('');
    var resultnumbercounttext = resultsumnumbercounttextlength.substring(0, sumresultsumnumbercounttextlength).concat('', '.' + arrayresultsumnumbercounttextlength[arrayresultsumnumbercounttextlength.length - 1] + numberunit);
    return resultnumbercounttext;
}

