$('.blogpostfeed').on('click', '.viewimagevideo_blogpost', function () {
    var postid = $(this).closest('.blogpost').first().attr('id');
    var imagevideoid = $(this).attr('id');
    var imagevideocount = imagevideoid.replace('imagevideocount', '');

    ListMediaId(postid)
        .then(function (ListMediaIdFunction) {
            if (ListMediaIdFunction.length != 0) {
                $('#carouselviewimagevideo .carousel-inner').empty();
                $.each(ListMediaIdFunction, function (i, mediaiddata) {
                    var valuecheckmediatype = checkmediatype(mediaiddata.type);
                    if (i == imagevideocount) {
                        if (valuecheckmediatype == 'image') {
                            $('.carousel_bg_blogpost').css({ 'background-color': '', 'background-image': 'url(/Image/blogpost/images/' + mediaiddata.url + ')' });
                            $('#carouselviewimagevideo .carousel-inner').append('<div class="carousel-item active"><img class="carousel_imagevideo_blogpost" src="/Image/blogpost/images/' + mediaiddata.url + '"></div>');
                        }
                        else if (valuecheckmediatype == 'video') {
                            $('.carousel_bg_blogpost').css({ 'background-color': 'var(--colorblack)', 'background-image': '' });
                            $('#carouselviewimagevideo .carousel-inner').append('<div class="carousel-item active"><video class="carousel_imagevideo_blogpost" src="/Image/blogpost/videos/' + mediaiddata.url + '" controls autoplay></video></div>');
                        }
                    }
                    else {
                        if (valuecheckmediatype == 'image') {
                            $('#carouselviewimagevideo .carousel-inner').append('<div class="carousel-item"><img class="carousel_imagevideo_blogpost" src="/Image/blogpost/images/' + mediaiddata.url + '"></div>');
                        }
                        else if (valuecheckmediatype == 'video') {
                            $('#carouselviewimagevideo .carousel-inner').append('<div class="carousel-item"><video class="carousel_imagevideo_blogpost" src="/Image/blogpost/videos/' + mediaiddata.url + '" controls></video></div>');
                        }
                    }
                });
            }

            $('.blogpostfeed').children().find('video').trigger('pause');
        })
        .catch(function (error) {
            console.error(error);
            throw error;
        });
});

$('#carouselviewimagevideo').on('slide.bs.carousel', event => {
    var indexto = event.to;
    var indexfrom = event.from;
    var mediaurl = $('#carouselviewimagevideo').find('.carousel-item').eq(indexto).find('img').attr('src');
    if (mediaurl != null) {
        $('.carousel_bg_blogpost').css({ 'background-color': '', 'background-image': 'url(' + mediaurl + ')' });
    }
    else {
        $('.carousel_bg_blogpost').css({ 'background-color': 'var(--colorblack)', 'background-image': '' })
    }
    $('#carouselviewimagevideo').find('.carousel-item').eq(indexfrom).find('video').trigger('pause');
    $('#carouselviewimagevideo').find('.carousel-item').eq(indexto).find('video').trigger('play');
});

