$('.search_layout').on('keypress', function (e) {
    var search_layout = $('.search_layout').val();

    if (search_layout != null && search_layout != '' || e.which == 13) {
        $('.blogpostfeed').empty();
        if (search_layout != null && search_layout != '') {
            var title = $('title').text().split(' ');
            if (title.length > 3) {
                title = title[0] + ' ' + title[1];
            }
            else {
                title = title[0];
            }

            ListMenuGroupId(title)
                .then(function (ListMenuGroupIdFunction) {
                    if (ListMenuGroupIdFunction.length > 0) {
                        $.each(ListMenuGroupIdFunction, function (i, listmenugroupiddata) {
                            var searchfromdata = new FormData();
                            searchfromdata.append('data', search_layout);

                            SearchPost(searchfromdata, listmenugroupiddata.id)
                                .then(function (SearchPostFunction) {
                                    if (SearchPostFunction.length > 0) {
                                        $.each(SearchPostFunction, function (i, searchpostdata) {
                                            DisplayGeneralCreatePost(searchpostdata.id);
                                        });

                                        $('#checksearchvalue').val('value');
                                    }
                                })
                                .catch(function (error) {
                                    console.error(error);
                                    throw error;
                                });
                        });
                    }
                    else {
                        var searchfromdata = new FormData();
                        searchfromdata.append('data', search_layout);

                        SearchPost(searchfromdata, 0)
                            .then(function (SearchPostFunction) {
                                if (SearchPostFunction.length > 0) {
                                    $.each(SearchPostFunction, function (i, searchpostdata) {
                                        DisplayGeneralCreatePost(searchpostdata.id);
                                    });

                                    $('#checksearchvalue').val('value');
                                }
                            })
                            .catch(function (error) {
                                console.error(error);
                                throw error;
                            });
                    }
                })
                .catch(function (error) {
                    console.error(error);
                    throw error;
                });
        }
        else {
            DisplayGeneralFeedPost();
            $('#checksearchvalue').val('');
        }
    }
    else {
        $('#checksearchvalue').val('');
    }
});

