$('.uploadfile_filemanage').on('change', function () {
    var files = $(this).prop('files');
    var filefromdata = new FormData();
    if (files.length > 0) {
        for (var i = 0; i < files.length; i++) {
            var checkattachmenttypeall = checkattachmenttype(files[i].type);
            var checkmediasize = files[i].size;
            if (checkattachmenttypeall == 'not type') {
                $('#ErrorBlogPost').modal('show');
                $('.headlabelerror_errorblogpostmodal').html('ชนิดไฟล์ไม่ถูกต้อง!');
                $('.labelerror_errorblogpostmodal').html('ระบบรองรับชนิดไฟล์ .docx, .doc, .xlsx, .xls, .pptx, .ppsx, .pdf, .jpg, .jpeg, .png, .mp4, .mp3 และ .zip เท่านั้น');
                $('.btnerror_errorblogpostmodal').attr({ 'data-bs-dismiss': 'modal' });
                return false;
            }
            else if (checkmediasize > 1000000000 /*1GB*/) {
                $('#ErrorBlogPost').modal('show');
                $('.headlabelerror_errorblogpostmodal').html('ขนาดไฟล์ไม่ถูกต้อง!');
                $('.labelerror_errorblogpostmodal').html('ระบบรองรับขนาดไฟล์ไม่เกิน 1 GB เท่านั้น');
                $('.btnerror_errorblogpostmodal').attr({ 'data-bs-dismiss': 'modal' });
                return false;
            }

            filefromdata.append(files[i].name, files[i]);
        }
    }
    var pathfolderid = $('#pathfolderid').val();

    UploadFile(pathfolderid, filefromdata)
        .then(function (UploadFileFunction) {
            if (UploadFileFunction != null) {
                $('#SuccessBlogPost').modal('show');
                $('.headlabelsuccess_successblogpostmodal').html('สำเร็จ!');
                $('.labelsuccess_successblogpostmodal').html('คุณได้อัพโหลดไฟล์สำเร็จแล้ว');
                $('.btnsuccess_successblogpostmodal').attr({ 'data-bs-dismiss': 'modal' });
                $('.uploadfile_filemanage').val('');
            }
        })
        .catch(function (error) {
            console.error(error);
            throw error;
        });
});

